<template>
    <div class="weui-loading_toast" v-show="show">
        <div class="weui-mask_transparent"></div>
        <div class="weui-toast" :style="{ position: position }">
            <i class="weui-loading weui-icon_toast"></i>
            <p class="weui-toast__content">{{ text || '加载中' }}<slot></slot></p>
        </div>
    </div>
</template>
<script>
  export default {
    props: {
      show: {
        type: Boolean,
        default: false
      },
      text: String,
      position: String
    },
    created () {
      this.shows = this.show
    },
    data () {
      return {
        shows: false
      }
    },
    watch: {
      show (val) {
        this.shows = val
      },
      shows (val) {
        this.$emit('input', val)
      }
    }
  }
</script>

